# -----------------------------------------------------------------------------
#       creation des references de table
# -----------------------------------------------------------------------------


alter table bus 
  add foreign key fk_bus_typebus (idtypebus)
      references typebus (idtypebus) ;


alter table filiale 
  add foreign key fk_filiale_agence (idagence)
      references agence (idagence) ;



alter table reservation 
  add foreign key fk_reservation_voyage (idvoyage)
      references voyage (idvoyage) ;


alter table reservation 
  add foreign key fk_reservation_user (iduser)
      references user (iduser) ;


alter table reservation 
  add foreign key fk_reservation_mode (idmode)
      references mode (idmode) ;



alter table voyage 
  add foreign key fk_voyage_user (iduser)
      references user (iduser) ;


alter table voyage 
  add foreign key fk_voyage_bus (idbus)
      references bus (idbus) ;

